graphql.query:
query {
    getFeed(
        limit: 10,
        page: 0
        #only: rooms || forums || posts
    ) {
        posts {
            id,
            author {
                id,
                name,
                account_type,
                profile_photo_url,
                is_bot
            },
            title,
            body,
            type,
            attachments {
                author {
                    id,
                    name,
                    name_type,
                    profile_photo_url
                },
                type,
                data
            },
            likes,
            dislikes,
            shares,
            interest,
            country_code,
            createdAt,
            updatedAt,
            topics,
            index_type,
            index
        },
        rooms {
            id,
            title,
            description,
            author {
                id,
                name,
                account_type,
                profile_photo_url
            },
            settings {
                    video,
                    private,
                    audio,
                    chat
            },
            participants {
                id,
                name,
                name_type,
                profile_photo_url
            },
            interests,
            blacklist {
                id,
                name,
                name_type,
                profile_photo_url
            },
            moderators {
                id,
                name,
                name_type,
                profile_photo_url
            },
            broadcasters {
                id,
                name,
                name_type,
                profile_photo_url
            },
            createdAt,
            updatedAt,
            index_type,
            index,
            #template,
            live
        }
    }
}

